export class Timer{pauseAmount=0;beginTime=null;paused=!0;constructor(a,b){this.duration=a,this.options=b||{}}formatTime(b,a){if("ms"===(a=(a=a||"ms").toLowerCase()))return b;if(a.includes("yyyy")){let c=Math.floor(b/31536e6);a=a.replace("yyyy",c),b-=31536e6*c}else if(a.includes("yy")){let d=Math.floor(b/31536e6);a=a.replace("yy",String(d).slice(-2)),b-=31536e6*d}if(a.includes("mo")){let e=Math.floor(b/2592e6);a=a.replace("mo",e),b-=2592e6*e}if(a.includes("ww")){let f=Math.floor(b/6048e5);a=a.replace("ww",f),b-=6048e5*f}if(a.includes("dd")){let g=Math.floor(b/864e5);a=a.replace("dd",g),b-=864e5*g}if(a.includes("hh")){let h=Math.floor(b/36e5);a=a.replace("hh",h),b-=36e5*h}if(a.includes("mm")){let i=Math.floor(b/6e4);a=a.replace("mm",i),b-=6e4*i}if(a.includes("ss")){let j=Math.floor(b/1e3);a=a.replace("ss",j),b-=1e3*j}if(a.includes("ms")){let k=b;a=a.replace("ms",k)}return a}runningTime(a){return!0===this.paused?this.formatTime(this.pauseAmount,a):this.formatTime(this.pauseAmount+(Date.now()-this.beginTime),a)}timeLeft(a){return this.formatTime(this.duration-this.runningTime(),a)}isRunning(){return!0!==this.paused}start(){return this.paused=!1,this.beginTime=Date.now(),this}resume(){return this.start()}stop(){return!0!==this.paused&&(this.paused=!0,this.pauseAmount+=Date.now()-this.beginTime),this}pause(){return this.stop()}reset(){return this.pauseAmount=0,this.paused=!0,this.beginTime=null,this}}